mapper.select:
SELECT * from room r where r.room_Id != 0 and r.room_peo < r.room_num and r.room_rate_high >= #{userRate} and #{userRate} >= r.room_rate_low and r.room_type = #{type}
and r.room_mode = #{mode}
order by r.room_date desc

mapper.update:
UPDATE room r SET
r.room_peo = r.room_peo + 1
where r.room_id = #{RoomId}
